mapper.select:
SELECT A.* , TO_CHAR(NVL((SELECT MAX(TO_NUMBER(BPRICE)) FROM BID_LIST B WHERE B.ANUM = A.ANUM), REPLACE(APRICEMIN,',')),'FM9,999,999') AS BIDHIGHPRICE --현재 최고가격 FROM(SELECT A.ANUM AS ANUM --상품번호 ,A.ASORT AS ASORT --상품구분(3: 경매) ,A.ATITLE AS ATITLE --제목 ,A.ALOC AS ALOC --지역 ,A.AREPHOTO AS AREPHOTO --썸네일 사진 ,A.APRICEMIN AS APRICEMIN--최소가격 ,A.AENDTIME AS AENDTIME --종료시간 ,A.ASTATUS AS ASTATUS --상품상태 ,A.ADELYN AS ADELYN --삭제여부 ,TO_CHAR(TO_DATE(A.AREGDATE),'YYYY/MM/DD') AS AREGDATE --등록일 ,A.MNUM AS MNUM --회원번호 ,A.CGNUM AS CGNUM --카테고리번호 ,CEIL(ROW_NUMBER() OVER(ORDER BY A.ANUM DESC) /#{pagesize}) AS CURPAGE --현재페이지 ,COUNT(A.ANUM) OVER() AS TOTALCOUNT --조회된 총 상품 수 FROM AUCTION_GOODS A WHERE A.ADELYN ='N'
AND CGNUM = #{cgnum}
AND ( TO_CHAR(TO_DATE(AREGDATE),'YY/MM/DD')>=TO_CHAR(TO_DATE(#{dateFrom}),'YY/MM/DD') AND TO_CHAR(TO_DATE(AREGDATE),'YY/MM/DD')<=TO_CHAR(TO_DATE(#{dateTo}),'YY/MM/DD') )
AND A.ATITLE LIKE '%' || #{atitle} || '%'
ORDER BY A.ANUM DESC ) A WHERE CURPAGE = #{curpage}
