mapper.select:
SELECT * FROM ( SELECT MNUM ,CGNUM ,GNUM ,GSORT ,GTITLE ,GLOC ,GREPHOTO ,TO_CHAR(GPRICE, 'FM9,999,999,999') AS GPRICE ,GCONTENTS ,GSTATUS ,GDELYN ,TO_CHAR(GREGDATE, 'YYYY-MM-DD') AS GREGDATE ,TO_CHAR(GUPDATE, 'YYYY-MM-DD') AS GUPDATE ,CEIL(ROW_NUMBER() OVER(ORDER BY A.GNUM DESC) / #{pagesize} ) AS CURPAGE ,COUNT(A.GNUM) OVER() AS TOTALCOUNT --총 데이터 수 ,NVL(NULL,#{pagesize}) AS PAGESIZE ,NVL(NULL, #{groupsize}) AS GROUPSIZE FROM GENERAL_GOODS A WHERE GDELYN = 'N' AND GSORT = '1'
AND CGNUM = #{cgnum}
AND ( TO_CHAR(TO_DATE(GREGDATE),'YY/MM/DD')>=TO_CHAR(TO_DATE(#{dateFrom}),'YY/MM/DD') AND TO_CHAR(TO_DATE(GREGDATE),'YY/MM/DD')<=TO_CHAR(TO_DATE(#{dateTo}),'YY/MM/DD') )
AND GTITLE LIKE '%' || #{gtitle} || '%'
ORDER BY GNUM DESC ) -- ? : CURPAGE 현재 페이지 WHERE CURPAGE = #{curpage}

SELECT * FROM ( SELECT MNUM ,CGNUM ,GNUM ,GSORT ,GTITLE ,GLOC ,GREPHOTO ,TO_CHAR(GPRICE,'FM9,999,999,999') GPRICE ,GCONTENTS ,GSTATUS ,GDELYN ,TO_CHAR(GREGDATE, 'YYYY-MM-DD') AS GREGDATE ,TO_CHAR(GUPDATE, 'YYYY-MM-DD') AS GUPDATE ,CEIL(ROW_NUMBER() OVER(ORDER BY A.GNUM DESC) / #{pagesize} ) AS CURPAGE ,COUNT(A.GNUM) OVER() AS TOTALCOUNT --총 데이터 수 ,NVL(NULL,#{pagesize}) AS PAGESIZE ,NVL(NULL, #{groupsize}) AS GROUPSIZE FROM GENERAL_GOODS A WHERE GDELYN = 'N' AND GSORT = '2'
AND CGNUM = #{cgnum}
AND ( TO_CHAR(TO_DATE(GREGDATE),'YY/MM/DD')>=TO_CHAR(TO_DATE(#{dateFrom}),'YY/MM/DD') AND TO_CHAR(TO_DATE(GREGDATE),'YY/MM/DD')<=TO_CHAR(TO_DATE(#{dateTo}),'YY/MM/DD') )
AND GTITLE LIKE '%' || #{gtitle} || '%'
ORDER BY GNUM DESC ) -- ? : CURPAGE 현재 페이지 WHERE CURPAGE = #{curpage}

SELECT * FROM ( SELECT G.GNUM AS GNUM -- 일반 상품번호 ,G.GSORT AS GSORT -- 구분 ,G.GTITLE AS GTITLE -- 제목 ,G.GLOC AS GLOC -- 거래지역 ,G.GREGCODE AS GREGCODE -- 법정동코드 ,G.GPHOTO AS GPHOTO -- 사진 ,TO_CHAR(G.GPRICE,'FM9,999,999,999') AS GPRICE -- 가격 ,G.GCONTENTS AS GCONTENTS -- 설명 ,G.GSTATUS AS GSTATUS -- 상태 ,G.GDELYN AS GDELYN -- 삭제여부 ,G.GREGDATE AS GREGDATE -- 등록일 ,G.GUPDATE AS GUPDATE -- 수정일 ,G.MNUM AS MNUM -- 회원번호 ,G.CGNUM AS CGNUM -- 코드 ,CEIL(ROW_NUMBER() OVER(ORDER BY G.GNUM DESC)/#{pagesize, jdbcType=VARCHAR}) AS CURPAGE -- 페이지 번호 ,COUNT(G.GNUM) OVER() AS TOTALCOUNT -- 총 데이터 수 ,#{groupsize, jdbcType=VARCHAR} AS GROUPSIZE -- 그룹 사이즈 ,#{pagesize, jdbcType=VARCHAR} AS PAGESIZE -- 페이지 사이즈 FROM GENERAL_GOODS G
WHERE SUBSTR(G.GREGCODE, 0, 8) = SUBSTR(#{gregcode, jdbcType=VARCHAR}, 0, 8)
AND G.CGNUM = #{cgnum, jdbcType=VARCHAR}
AND G.GSORT = #{gsort, jdbcType=VARCHAR}
AND TO_CHAR(TO_DATE(G.GREGDATE), 'YYYY/MM/DD') >= TO_CHAR(TO_DATE(#{dateFrom, jdbcType=VARCHAR}), 'YYYY/MM/DD')
AND TO_CHAR(TO_DATE(G.GREGDATE), 'YYYY/MM/DD') <= TO_CHAR(TO_DATE(#{dateTo, jdbcType=VARCHAR}), 'YYYY/MM/DD')
AND G.GTITLE LIKE '%' || #{gtitle, jdbcType=VARCHAR} || '%'
) WHERE CURPAGE = #{curpage, jdbcType=VARCHAR}

SELECT GC.REGCODE AS REGCODE ,SUM(GC.SAJA) AS SAJA ,SUM(GC.PANDA) AS PANDA FROM ( SELECT SUBSTR(G.GREGCODE, 0, 8) || '00' AS REGCODE ,DECODE(G.GSORT, '1', 1, 0) AS SAJA ,DECODE(G.GSORT, '2', 1, 0) AS PANDA FROM GENERAL_GOODS G
WHERE SUBSTR(G.GREGCODE, 0, 5) = SUBSTR(#{regcode, jdbcType=VARCHAR}, 0, 5) AND SUBSTR(G.GREGCODE, -5, 3) != '000' AND G.GSTATUS = '1' AND TO_CHAR(TO_DATE(G.GREGDATE), 'YYYY/MM/DD') >= TO_CHAR(TO_DATE(ADD_MONTHS(SYSDATE, -1)), 'YYYY/MM/DD')
) GC GROUP BY GC.REGCODE
